import React, {useEffect, useState} from "react";
import axios from "axios";
const API_PATH = 'http://localhost/backend/getautok.php';

function EffectTutorial() {
    const [data, setData] = useState("");
    const [data2, setData2] = useState("");
    const [data3, setData3] = useState("");
    const [data4, setData4] = useState("");
    const [count, setCount] = useState(1);
    useEffect(() => {
        axios.get('http://localhost/backend/getautok.php', {
            headers: {
              'Access-Control-Allow-Origin:': '*'
            }
          })
        .then((response)=>{
            console.log("API meghívódott!");
            console.log(response.data);
            setData(response.data[0].id);
            setData2(response.data[0].szoveg);
            setData3(response.data[0].linkkep);
            setData4(response.data[0].ar);
    });
    }, []);
    return (
    <div>Hello World 
        <h1>ID: {data}</h1>
        <h1>Szöveg: {data2}</h1>
        <h1>Linkkép: {data3}</h1>
        <h1>Ár: {data4}</h1>
        <h1>{count}</h1>
        <button
        onClick={()=>{
            setCount(count +1);
        }}
        >Kattints ide!</button>
        </div>);
}

export default EffectTutorial;
